////////////////////////////////////////////////////class TVXVideoRectangle: public TVideoRectangle	////	//// This class represents a generic video rectangle.	//// It is common for video hardware to show live video	//// (or some other video source) in a rectangular hardware	//// window. This allows for the source and rectangle bounds	//// to be set.	////	{	public:		enum ERectangle {	kRectangle1	= 1<<0,							kRectangle2	= 1<<1,							kRectangle3	= 1<<2,							kRectangle4	= 1<<3,							kRectangleNone = 0							};		enum ERectangleFill {								kNormalYNormalXInside		= 0,							kNormalYNormalXOutside		= 1,							kNormalYInvertedXInside		= 2,							kNormalYInvertedXOutside	= 3,							kInvertedYNormalXInside		= 4,							kInvertedYNormalXOutside	= 5,							kInvertedYInvertedXInside	= 6,							kInvertedYInvertedXOutside	= 7,								kInside	= kNormalYNormalXInside,		// Useful aliases							kOutside =  kNormalYNormalXOutside							};	public:		TVXVideoRectangle();		TVXVideoRectangle(TVXVideoDevice *device, ERectangle	RectID);		TVXVideoRectangle(const TVXVideoRectangle& from);				// Copy constructor		virtual ~TVXVideoRectangle();		TVXVideoRectangle& operator=(const TVXVideoRectangle& from);	// Assignment operator		void SetRectangleSize(int top, int left, int bottom, int right);		void SetRectangleSource(TSource source);		virtual void SetRectangleGeometry(int top, int left, int bottom, int right, ERectangleFill fill);		virtual void SetRectanglePriority(int priority);	private:		TVXVideoDevice	*VX;		ERectangle		whichRect; 	};////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////#endif