// Timecode utilities// #include "TimeCodeUtilities.h"voidTimeCode_long_2_N19(TC_N19Ptr tc,long a){char c,d;// si tc =-1 vide on place 00 00 00 00	if (a == 0xffffffff){		tc->h=0;		tc->mn=0;		tc->s=0;		tc->f=0;		return;	}//  tc=23:59:59:29// conversion// images			c=a&0xf;	a=a>>4;	d=a&0x3;	tc->f=c+d*10;	a=a>>4;		// secondes	c=a&0xf;	a=a>>4;	d=a&0x7;	tc->s=c+d*10;	a=a>>4;		// minutes	c=a&0xf;	a=a>>4;	d=a&0x7;	tc->mn=c+d*10;	a=a>>4;		// heures	c=a&0xf;	a=a>>4;	d=a&0x3;	tc->h=c+d*10;	a=a>>4;	} //	long  TimeCode_N19_2_long(TC_N19Ptr tc){long a,b,c=0;	a=tc->h;	b=a/10;	c=b<<4;	c|=a-b*10;	c=c<<4;		a=tc->mn;	b=a/10;	c|=b;	c=c<<4;	c|=a-b*10;	c=c<<4;				a=tc->s;	b=a/10;	c|=b;	c=c<<4;	c|=a-b*10;	c=c<<4;				a=tc->f;	b=a/10;	c|=b;	c=c<<4;	c|=a-b*10;	return c;		} //	// convertie en tc depuis un frame count absolulong TimeCode_Converted_FrameCount_2_TimeCode(long frameCount){TC_N19 n19;long timebase=600;long fps=25;long temp=frameCount;				n19.h=temp/(timebase*3600); 	// les heures	temp-=n19.h*(timebase*3600);		n19.mn=temp/(timebase*60); 		// les minutes	temp-=n19.mn*(timebase*60);			n19.s=temp/(timebase); 			// les secondes	temp-=n19.s*(timebase);			n19.f=temp*fps/timebase; 		// les frames		return TimeCode_N19_2_long(&n19);}long TimeCode_Converted_TimeCode_2_FrameCount(long timecode){TC_N19 n19;long TimeCode=0;long timebase=600;long ret;long FPS=25;	TimeCode_long_2_N19(&n19,timecode); // conversion		ret=n19.f*timebase/25; 	// les frames	ret+=n19.s*timebase; 			// les secondes	ret+=n19.mn*timebase*60; 		// les minutes	ret+=n19.h*timebase*3600; 		// les heures		return ret;}//end of file